mapper.select:
SELECT id, interfaceId, level, submitMessage, replyMessage, status, createTime, updateTime
FROM windy_api.order
WHERE userId = #{userId}
ORDER BY id
LIMIT ${(pageNum - 1) * 10}, 10;

SELECT id, userId, level, submitMessage, status, createTime FROM windy_api.order
WHERE status = 0 WHERE status = 1
ORDER BY id LIMIT ${(pageNum - 1) * 10}, 10;

mapper.update:
UPDATE windy_api.order
SET replyMessage = #{replyMessage}, status = 1
WHERE id = #{orderId};

mapper.select:
SELECT userId
FROM windy_api.order
WHERE id = #{id};
