mapper.select:
SELECT id, nickname, avatar, gender, role, password, createTime
FROM windy_api.user
WHERE account = #{account} AND isDelete = false;

SELECT nickname
FROM windy_api.user
WHERE id = #{id};

mapper.update:
UPDATE windy_api.user
SET avatar = #{avatar}
WHERE id = #{id};

mapper.select:
SELECT email
FROM windy_api.user
WHERE id = #{id};

mapper.update:
UPDATE windy_api.user
SET password = #{newPassword}
WHERE id = #{id};

UPDATE windy_api.user
nickname = #{nickname}, gender = #{gender}, email = #{email},
WHERE id = #{id};

mapper.select:
SELECT id, email
FROM windy_api.user
WHERE account = #{account};

SELECT id, secretKey
FROM windy_api.user
WHERE accessKey = #{accessKey};
